import sympy as sp

def solution():
    x = sp.symbols('x')
    equation = x - 1/x - 3
    solution = sp.solve(equation, x, dict=True)

    if solution:
        x_value = solution[0][x]
        result = (x_value - 1/x_value)**4
        return sp.simplify(result)
    return "No solution found"
