   def solve_diff_eq(x, t):
       return x**4 - x

   # Step 2: Use the Laplace transform to solve the equation
   def laplace_transform(x, t, s):
       return x**4 / (s**4 + 1)

   # Step 3: Inverse Laplace transform the result
   def inverse_laplace_transform(x, t, s):
